import React from "react";
import styled from "styled-components";
import colors from "./../../theme/colors";

const StyledLi = styled.li`
  width: 100%;
  list-style: none;
  display: flex;
  align-items: center;
  justify-content: space-evenly;
  border: 1px solid
    ${props =>
      props.sex ? props.colors.womenBorderColor : props.colors.menBorderColor};
  box-shadow: 0 0 20px -19px ${props => (props.sex ? props.colors.womenBorderColor : props.colors.menBorderColor)};
  margin: 0 auto 10px auto;
  padding: 5px;
  border-radius: 25px;
  overflow: hidden;

  &:last-of-type {
    margin: unset;
  }

  div {
    height: 40px;
    width: 40px;
    border-radius: 50%;
    background-color: ${props => props.colors.imgBackgroundColor};
    overflow: hidden;
    img {
      height: 100%;
      width: 100%;
      object-fit: cover;
    }
  }

  h1,
  p {
    display: flex;
    flex-direction: column;

    span {
      font-size: 10px;
      font-weight: 300;
      text-transform: uppercase;
      color: grey;
    }
  }
`;

const User = ({ userData }) => {
  const {
    address: { city, street, houseNumber },
    _id,
    name,
    email,
    sex,
    avatar,
    age,
    __v
  } = userData;

  return (
    <StyledLi sex={sex} colors={colors}>
      <div>
        <img src={avatar} alt={`${name}-image`} />
      </div>
      <h1>
        <span>ImiÄ™ i nazwisko</span>
        {name}
      </h1>
      <p>
        <span>Adres zamieszkania</span>
        {city}, ul.{street} {houseNumber}
      </p>
      <p>
        <span>E-mail</span>
        <a href={`mailto:${email}`}>{email}</a>
      </p>
      <p>
        <span>Wiek</span>
        {age} lat
      </p>
    </StyledLi>
  );
};

export default User;
