import React, { useEffect, useState } from "react";
import styled from "styled-components";
import SearchSection from "./components/SearchSection/SearchSection";
import DisplaySection from "./components/DisplaySection/DisplaySection";
import colors from "./theme/colors";

const StyledWrapper = styled.main`
  min-height: 100vh;
  width: 100vw;
  background: linear-gradient(
    45deg,
    ${props => props.colors.mainColor},
    rgb(244, 244, 244),
    ${props => props.colors.mainColor}
  );
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  position: relative;
`;

const App = () => {
  const [clearSort, setClearSort] = useState(false);
  const [usersData, setUsersData] = useState([]);
  const [searchValue, setSearchValue] = useState("");
  const [filteredData, setFilteredData] = useState([]);
  const [filterErr, setFilterErr] = useState(false);
  const [sortAgeValue, setSortAgeValue] = useState(true);
  const [sortEmailValue, setSortEmailValue] = useState(true);

  useEffect(() => {
    const fetchData = async () =>
      fetch("./clients.json")
        .then(response => response.json())
        .then(data => setUsersData(data.data))
        .catch(err => console.log(err));
    fetchData();
  }, []);

  useEffect(() => {
    if (searchValue === "") {
      setFilterErr(false);
      setFilteredData(usersData);
    } else if (searchValue !== "" && filteredData.length === 0) {
      setFilterErr(true);
      setFilteredData(
        usersData.filter(userData =>
          userData.name.toUpperCase().includes(searchValue.toUpperCase())
        )
      );
    } else {
      setFilterErr(false);
      setFilteredData(
        usersData.filter(userData =>
          userData.name.toUpperCase().includes(searchValue.toUpperCase())
        )
      );
    }
  }, [searchValue, usersData, filteredData.length]);

  useEffect(() => {
    const sortDataByAge = () => {
      if (!sortAgeValue) {
        filteredData.sort((a, b) => {
          if (a.age > b.age) {
            return 1;
          } else if (a.age < b.age) {
            return -1;
          } else return 0;
        });
      } else if (!sortAgeValue) {
        filteredData.sort((a, b) => {
          if (a.age > b.age) {
            return -1;
          } else if (a.age < b.age) {
            return 1;
          } else return 0;
        });
      }
    };

    const sortDataByEmail = () => {
      if (sortEmailValue) {
        filteredData.sort((a, b) => {
          if (a.email > b.email) {
            return 1;
          } else if (a.email < b.email) {
            return -1;
          } else return 0;
        });
      } else if (!sortEmailValue) {
        filteredData.sort((a, b) => {
          if (a.email > b.email) {
            return -1;
          } else if (a.email < b.email) {
            return 1;
          } else return 0;
        });
      }
    };

    sortDataByEmail();
    sortDataByAge();
  }, [sortAgeValue, filteredData, sortEmailValue]);

  const handleInputChange = e => {
    setSearchValue(e.target.value);
  };

  const handleButtonClick = e => {
    e.preventDefault();
    if (e.target.id === "age") {
      // TRUE ASCENDING ORDER
      setSortAgeValue(!sortAgeValue);
    } else if (e.target.id === "email") {
      // TRUE A-Z
      setSortEmailValue(!sortEmailValue);
    } else if (e.target.id === "clear") {
      // TRUE CLEAR
      setClearSort(!clearSort);
      e.target.name = clearSort ? "Resetuj filtr" : "Zastosuj filtr";
    } else return undefined;
  };

  return (
    <StyledWrapper colors={colors}>
      <SearchSection
        sortAgeValue={sortAgeValue}
        sortEmailValue={sortEmailValue}
        handleButtonClick={handleButtonClick}
        handleInputChange={handleInputChange}
        searchValue={searchValue}
        clearSort={clearSort}
      />
      <DisplaySection filterErr={filterErr} filteredData={filteredData} />
    </StyledWrapper>
  );
};

export default App;
