import React from "react";
import styled from "styled-components";
import colors from "./../../theme/colors";
import Button from "./../Button/Button";

const StyledSection = styled.section`
  background-color: ${props => props.colors.sectionBackgroundColor};
  padding: 25px 25px 5px 25px;
  height: 100px;
  width: 450px;
  border-top-left-radius: 25px;
  border-top-right-radius: 25px;
  display: flex;
  justify-content: space-between;
  align-items: center;

  form {
    display: flex;
    align-items: center;
    width: 100%;
    justify-content: space-between;

    label {
      width: 45%;
    }

    input {
      border: 1px solid ${props => props.colors.mainColor};
      padding: 10px;
      outline: none;
      width: 100%;
    }

    select {
      width: 100%;
    }
  }
`;

const SearchSection = ({
  handleInputChange,
  searchValue,
  sortEmailValue,
  sortAgeValue,
  handleButtonClick,
  clearSort
}) => {
  return (
    <StyledSection colors={colors}>
      <form>
        <label>
          <p>Wyszukaj użytkownika</p>
          <input value={searchValue} onChange={handleInputChange} type="text" />
        </label>
        <p>Sortuj według</p>
        <Button
          id="email"
          value={sortEmailValue}
          name="Name"
          func={handleButtonClick}
        />
        <Button
          id="age"
          value={sortAgeValue}
          name="1"
          func={handleButtonClick}
        />
        <Button
          id="clear"
          value={clearSort}
          name="3"
          func={handleButtonClick}
        />
      </form>
    </StyledSection>
  );
};

export default SearchSection;
